set client_encoding = 'UTF8';

drop table if exists erratas_packages;
drop table if exists erratas_channel;
drop table if exists erratas_bugzilla;
drop table if exists packages_files;
drop table if exists packages_changes;
drop table if exists packages_channel;
drop table if exists bugzilla;
drop table if exists erratas;
drop table if exists packages;
drop table if exists channels;

create table channels (
	channel_label varchar(128) not null primary key,
	channel_name varchar(128) not null,
	channel_parent_label varchar(128),
	channel_end_of_life timestamp,
	channel_arch varchar(64),
	channel_sync_packages timestamp,
	channel_sync_erratas timestamp,
	channel_supported boolean
	);

create table erratas (
	errata_advisory varchar(64) not null primary key,
	errata_issue_date timestamp,
	errata_update_date timestamp,
	errata_synopsis text,
	errata_advisory_type varchar(64),
	errata_last_modified_date timestamp
	);

create table erratas_channel (
	errata_advisory  varchar(64) not null,
 	channel_label varchar(128) not null,
	foreign key (errata_advisory) references erratas (errata_advisory),
	foreign key (channel_label) references channels (channel_label)
);

create table packages (
    package_id integer not null primary key,
    package_name varchar(128) not null,
    package_version varchar(64) not null,
    package_release varchar(64) not null,
    package_epoch varchar(64),
    package_arch_label varchar(16) not null,
    package_last_modified timestamp
);

create table erratas_packages (
	errata_advisory varchar(64) not null,
	package_id integer not null,
	--foreign key (package_id) references packages (package_id)
	foreign key (errata_advisory) references erratas (errata_advisory)
);

create table bugzilla (
	bugzilla_id integer not null primary key,
	bug_summary text not null
);

create table erratas_bugzilla (
	errata_advisory varchar(64) not null,
	bugzilla_id integer not null,
	foreign key (errata_advisory) references erratas (errata_advisory),
	foreign key (bugzilla_id) references bugzilla (bugzilla_id)
);

create table packages_channel (
	package_id integer not null,
	channel_label varchar(128) not null, 
	package_latest boolean,
	foreign key (package_id) references packages (package_id),
	foreign key (channel_label) references channels (channel_label)
);

create table packages_changes (
    package_change_pk char(64) not null primary key,
	package_id integer not null,
	entry_author varchar(256), 
	entry_date timestamp,
	entry_text text,
	foreign key (package_id) references packages (package_id)
);

create table packages_files (
    package_file_pk char(64) not null primary key,
	package_id integer not null,
	file_path varchar(256), 
	file_type varchar(256), 
	file_last_modified_date timestamp, 
	file_md5sum char(64),
	file_checksum char(64),
	file_size integer, 
	file_linkto varchar(256),
	foreign key (package_id) references packages (package_id)
);

